// function for rendering project cards data
const showCards = () => {
  let output = "";
  projects.forEach(({ title, cardImage, description, Previewlink, Githublink }) => {
    output += `
  <div class="column skill-card card" data-aos="zoom-in-up">
    <div class="project-card">
      <div class="project-img-wrapper">
        <img src="${cardImage}" class="project-img" alt="${title}">
        <div class="project-overlay">
          <h2 class="project-title overlay-title">${title}</h2>
          <p class="project-desc">${description}</p>
        </div>
      </div>
      <h2 class="project-title below-title">${title}</h2>
    </div>
  </div>`;


  });
  document.querySelector(".projectcards").innerHTML = output;
};



document.addEventListener("DOMContentLoaded", showCards);

function myFunction() {
  // Declare variables
  var input, button, i, skillcard, card, title;
  input = document.getElementById("myInput").value;
  input = input.toUpperCase();
  skillcard = document.getElementsByClassName("skill-card");
  card = document.getElementsByClassName("card");
  title = document.getElementsByClassName("title");

  // Loop through all list items, and hide those who don't match the search query
  for (i = 0; i < button.length; i++) {
    if (
      button[i].innerHTML.toUpperCase().includes(input) ||
      title[i].innerHTML.toUpperCase().includes(input)
    ) {
      skillcard[i].style.display = "";
      card[i].style.display = "";
    } else {
      skillcard[i].style.display = "none";
      card[i].style.display = "none";
    }
  }
}
